mapper.delete:

delete from evidence
where evidenceId = #{evidenceid,jdbcType=INTEGER}
  and replyId = #{replyid,jdbcType=INTEGER}
  and creatorId = #{creatorid,jdbcType=CHAR}

mapper.insert:

insert into evidence (evidenceId, replyId, creatorId, 
  topicId, picture, handlePic, 
  description, createTime, enable
  )
values (#{evidenceid,jdbcType=INTEGER}, #{replyid,jdbcType=INTEGER}, #{creatorid,jdbcType=CHAR}, 
  #{topicid,jdbcType=INTEGER}, #{picture,jdbcType=VARCHAR}, #{handlepic,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{enable,jdbcType=TINYINT}
  )

mapper.update:

update evidence
set topicId = #{topicid,jdbcType=INTEGER},
  picture = #{picture,jdbcType=VARCHAR},
  handlePic = #{handlepic,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR},
  createTime = #{createtime,jdbcType=TIMESTAMP},
  enable = #{enable,jdbcType=TINYINT}
where evidenceId = #{evidenceid,jdbcType=INTEGER}
  and replyId = #{replyid,jdbcType=INTEGER}
  and creatorId = #{creatorid,jdbcType=CHAR}

mapper.select:

select evidenceId, replyId, creatorId, topicId, picture, handlePic, description, 
createTime, enable
from evidence
where evidenceId = #{evidenceid,jdbcType=INTEGER}
  and replyId = #{replyid,jdbcType=INTEGER}
  and creatorId = #{creatorid,jdbcType=CHAR}
