mapper.delete:

delete from history_group
where userId = #{userid,jdbcType=CHAR}
  and groupId = #{groupid,jdbcType=INTEGER}

mapper.update:

update history_group
set title = #{title,jdbcType=VARCHAR},
  lastVisitTime = #{lastvisittime,jdbcType=TIMESTAMP}
where userId = #{userid,jdbcType=CHAR}
  and groupId = #{groupid,jdbcType=INTEGER}

mapper.select:

select userId, groupId, title, lastVisitTime
from history_group
where userId = #{userid,jdbcType=CHAR}
  and groupId = #{groupid,jdbcType=INTEGER}
