mapper.delete:

delete from reply_hot_req
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and type = #{type,jdbcType=INTEGER}

mapper.update:

update reply_hot_req
set enable = #{enable,jdbcType=TINYINT}
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and type = #{type,jdbcType=INTEGER}

mapper.select:

select replyId, userId, type, enable
from reply_hot_req
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and type = #{type,jdbcType=INTEGER}
