mapper.delete:

delete from reply
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and pointId = #{pointid,jdbcType=INTEGER}

mapper.update:

update reply
set topicId = #{topicid,jdbcType=INTEGER},
  createTime = #{createtime,jdbcType=TIMESTAMP},
  zanCount = #{zancount,jdbcType=INTEGER},
  opposeCount = #{opposecount,jdbcType=INTEGER},
  enable = #{enable,jdbcType=TINYINT}
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and pointId = #{pointid,jdbcType=INTEGER}

mapper.select:

select replyId, userId, pointId, topicId, createTime, zanCount, opposeCount, enable
from reply
where replyId = #{replyid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=CHAR}
  and pointId = #{pointid,jdbcType=INTEGER}
